const { expect } = require("chai");
var request = require("request");

describe("Cuisine GET API Testing", function () {
  var url = "http://localhost:3000/api/cuisines";

  it("returns status 200 to check if api works", function (done) {
    request(url, function (error, response, body) {
      expect(response.statusCode).to.equal(200);
      done();
    });
  });

  it("returns statusCode key in body to check if api give right result should be 200", function (done) {
    request(url, function (error, response, body) {
      body = JSON.parse(body);
      expect(body.statusCode).to.equal(200);
      done();
    });
  });

  it("check response data pass in array format", function (done) {
    request(url, function (error, response, body) {
      body = JSON.parse(body);
      expect(body.data).to.be.an("array");
      done();
    });
  });
});

describe("Cuisine POST API Testing", function () {
  var url = "http://localhost:3000/api/cuisine";
  let validRequest = {
    title: "Butter Chicken",
    title2: "A Creamy Delight from North India",
    subTitle:
      "Chicken, Butter, Tomato Puree, Cream, Ginger, Garlic, Garam Masala, Chili Powder, Fenugreek, Salt, Oil",
    // prettier-ignore
    path: "data:image/png;base64,",
    description:
      "Butter Chicken, or Murgh Makhani, is a popular North Indian dish where tender pieces of chicken are marinated in yogurt and spices, then simmered in a rich, creamy tomato sauce. It's best served with naan or basmati rice.",
  };

  let invalidRequest = {
    title: "",
    title2: "",
    subTitle: "",
    path: "",
    description: "",
  };

  it("Should Add a new Cuisine", function(done) {
    request.post({url:url,form:validRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(200);
      body = JSON.parse(body);
      expect(body.statusCode).to.equal(201);
      done();
    })
  });

  it("should return status 200 even Description is missing", function(done) {
    validRequest.description = "";
    request.post({url:url,form:validRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(200);
      done();
    })
  });

  it("should return status 400 when title is missing", function(done) {
    invalidRequest.title2 = "A Creamy Delight from North India",
    invalidRequest.subTitle = "Chicken, Butter, Tomato Puree, Cream, Ginger, Garlic, Garam Masala, Chili Powder, Fenugreek, Salt, Oil",
    invalidRequest.path = "data:image/png;base64,"
    request.post({url:url,form:invalidRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(400);
      done();
    })
  });

  it("should return status 400 when subtitle is missing", function(done) {
    invalidRequest.title = "Butter Chicken",
    invalidRequest.title2 = "",
    invalidRequest.subTitle = "Chicken, Butter, Tomato Puree, Cream, Ginger, Garlic, Garam Masala, Chili Powder, Fenugreek, Salt, Oil",
    invalidRequest.path = "data:image/png;base64,"
    request.post({url:url,form:invalidRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(400);
      done();
    })
  }); 

  it("should return status 400 when Dish Ingredient is missing", function(done) {
    invalidRequest.title = "Butter Chicken",
    invalidRequest.title2 = "A Creamy Delight from North India",
    invalidRequest.subTitle = "",
    invalidRequest.path = "data:image/png;base64,"
    request.post({url:url,form:invalidRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(400);
      done();
    })
  }); 

  it("should return status 400 when Image is missing", function(done) {
    invalidRequest.title = "Butter Chicken",
    invalidRequest.title2 = "A Creamy Delight from North India",
    invalidRequest.subTitle = "Chicken, Butter, Tomato Puree, Cream, Ginger, Garlic, Garam Masala, Chili Powder, Fenugreek, Salt, Oil",
    invalidRequest.path = ""
    request.post({url:url,form:invalidRequest}, function(error, response, body){
      expect(response.statusCode).to.equal(400);
      done();
    })
  }); 

  
});
